SELECT
  SUM(
    (
      SELECT
        COUNT(event_name) FILTER (
          WHERE
            (
              "Event Created By User Type" IN ('System', 'Service')
            )
        )
    )
  ) / SUM(
    (
      SELECT
        COUNT(event_name) FILTER (WHERE("Event Created By User Type" IS NOT NULL))
    )
  ) * 100
FROM
  THIS_PROCESS

SELECT
  SUM(
    (
      SELECT
        COUNT("Last FI Invoice ID") FILTER (
          WHERE
            (
              "Event Created By User Type" IN ('System', 'Service')
              AND (
                event_name LIKE '%Asset%'
                OR event_name IN (
                  'Update Capitalization Date',
                  'Update Deactivation Date'
                )
              )
            )
        )
    )
  ) / SUM(
    (
      SELECT
        COUNT("Last FI Invoice ID") FILTER (
          WHERE
            event_name LIKE '%Asset%'
            OR event_name IN (
              'Update Capitalization Date',
              'Update Deactivation Date'
            )
        )
    )
  ) * 100
FROM
  THIS_PROCESS

