metrics.signalFragment:
COUNT(DISTINCT "ARUniqueKey") FILTER (
	WHERE
		event_name = 'Create Accounts Receivable'
		AND "ClearingDate" IS NULL
		AND (NOW()-"DocumentDate")> DURATION ${p2c_s4hc_open_ar_period}
	)

COUNT(case_id) FILTER (
	WHERE (${itp_due_date} - ${itp_clearing_date} 
		> DURATION ${itp_early_payment_condition})
)
/COUNT(case_id) FILTER (
	WHERE (${itp_clearing_date} IS NOT NULL)
)* 100

SUM(
	(SELECT COUNT(event_name) FILTER (
		WHERE (event_name IN (${itp_clear_invoice_events})
			AND (${itp_automation_condition})))
	)
)
/ SUM(
	(SELECT COUNT(event_name) FILTER (
	WHERE (event_name IN (${itp_clear_invoice_events})))
	)
)
* 100

COUNT(case_id) FILTER (
	WHERE (${itp_clearing_date} - ${itp_due_date} 
		< DURATION ${itp_late_payment_condition}
	AND ${itp_due_date} - ${itp_clearing_date}
		< DURATION ${itp_early_payment_condition}
	)
)
/
COUNT(case_id) FILTER (
	WHERE (${itp_clearing_date}IS NOT NULL)
)
* 100

COUNT(case_id) FILTER (
	WHERE event_name MATCHES(NOT ${itp_po_creation_to_invoice_posting})
)

SUM(
	(${itp_amount_conv} * ${itp_discount_percentage})
	-${itp_discount_applied_to_invoice}
	)
FILTER(
	WHERE EVENT_NAME MATCHES (${itp_cd_1_missed})
		OR EVENT_NAME MATCHES (${itp_cd_2_missed})
	)

SUM(
	(${itp_amount_conv} * ${itp_discount_percentage})
	-${itp_discount_applied_to_invoice}
	)
FILTER(
	WHERE EVENT_NAME MATCHES (${itp_cd_in_time}))

COUNT(case_id) FILTER (
	WHERE (${itp_clearing_date} - ${itp_due_date} 
		> DURATION ${itp_late_payment_condition})
)
/COUNT(case_id) FILTER (
	WHERE (${itp_clearing_date} IS NOT NULL)
) * 100
