metrics.signalFragment:
COUNT(case_id) FILTER (
	WHERE ${ato_late_delivery_condition}
		AND ${ato_delivery_completed_condition}
	)

COUNT(case_id) FILTER (
	WHERE ${ato_late_delivery_condition}
		AND ${ato_delivery_completed_condition}
	)
/
COUNT(case_id) FILTER (
	WHERE ${ato_delivery_completed_condition}
	)
* 100

AVG(${ato_goods_receipt_date} - ${ato_delivery_due_date}) FILTER (
	WHERE ${ato_late_delivery_condition}
		AND ${ato_delivery_completed_condition}
	)

(1 - 
	COUNT(case_id) FILTER (
		WHERE ${ato_late_delivery_condition}
			AND ${ato_delivery_completed_condition}
		)
	/
	COUNT(case_id) FILTER (
		WHERE ${ato_delivery_completed_condition}
		)
)
* 100

COUNT(case_id) FILTER (
	WHERE ${ato_overdue_delivery_condition}
	)

COUNT(case_id) FILTER (
	WHERE ${ato_overdue_delivery_condition}
	)
/
COUNT(case_id) FILTER (
	WHERE (${ato_delivery_completed_condition}) IS NULL
	)
* 100

SUM((SELECT COUNT("case_id") FILTER (
		WHERE ( event_name = 'Create Asset Item')
 AND ("Event Created By User Type" IN ('System','Service')
))
	))
	/ SUM((SELECT COUNT("case_id") FILTER (
		WHERE ( event_name = 'Create Asset Item')
 ))) * 100

SUM((SELECT COUNT("case_id") FILTER (
		WHERE ("Event Created By User Type" IN ('System','Service')
	AND event_name = 'Create FI Invoice'
))
	))
	/ SUM((SELECT COUNT("case_id") FILTER (
		WHERE event_name = 'Create FI Invoice')
)) * 100

SUM((SELECT COUNT("case_id") FILTER (
		WHERE ("Event Created By User Type" IN ('System','Service')
 	AND event_name = 'Create PO Item'
  ))
	))
	/ SUM((SELECT COUNT("case_id") FILTER (
		WHERE event_name = 'Create PO Item'
 ))) * 100
