metrics.signalFragment:
SUM((
	SELECT COUNT("Event Id") FILTER (
		WHERE "Participating Suppliers" = 1 and "Awarded Total Cost" > 0
	)
))

AVG(
	(SELECT SUM("Participating Suppliers") FILTER (
		WHERE (event_name IN (${sourcing_event_creation_events})) 
        ))
	/(SELECT SUM("Invited Suppliers") FILTER (
		WHERE (event_name IN (${sourcing_event_creation_events})) 
        ))
)*100
