metrics.signalFragment:
SUM(
	(SELECT COUNT(event_name) FILTER (
		WHERE (event_name IN (${itc_ecc_s4h_clear_invoice_events})
			AND (${itc_ecc_s4h_automation_condition})))
	)
)
/ SUM(
	(SELECT COUNT(event_name) FILTER (
	WHERE (event_name IN (${itc_ecc_s4h_clear_invoice_events})))
	)
)
* 100

COUNT(case_id) FILTER (
	WHERE (${itc_ecc_s4h_clearing_date} - ${itc_ecc_s4h_due_date} 
		> DURATION ${itc_ecc_s4h_late_payment_condition})
)
/COUNT(case_id) FILTER (
	WHERE (${itc_ecc_s4h_clearing_date} IS NOT NULL)
) * 100

COUNT(case_id) FILTER (
	WHERE (${itc_ecc_s4h_due_date} - ${itc_ecc_s4h_clearing_date} 
		> DURATION ${itc_ecc_s4h_early_payment_condition})
)
/COUNT(case_id) FILTER (
	WHERE (${itc_ecc_s4h_clearing_date} IS NOT NULL)
)* 100

COUNT(case_id) FILTER (
	WHERE (${itc_ecc_s4h_clearing_date} - ${itc_ecc_s4h_due_date} 
		< DURATION ${itc_ecc_s4h_late_payment_condition}
	AND ${itc_ecc_s4h_due_date} - ${itc_ecc_s4h_clearing_date}
		< DURATION ${itc_ecc_s4h_early_payment_condition}
	)
)
/
COUNT(case_id) FILTER (
	WHERE (${itc_ecc_s4h_clearing_date} IS NOT NULL)
)
* 100
