metrics.signalFragment:
SUM(
	(SELECT COUNT(event_name) FILTER (
		WHERE (event_name IN (${i2c_s4hc_clear_invoice_events})
			AND (${i2c_s4hc_automation_condition})))
	)
)
/ SUM(
	(SELECT COUNT(event_name) FILTER (
	WHERE (event_name IN (${i2c_s4hc_clear_invoice_events})))
	)
)
 * 100

COUNT(case_id) FILTER (
	WHERE (${i2c_s4hc_due_date} - ${i2c_s4hc_clearing_date} 
		> DURATION ${i2c_s4hc_early_payment_condition})
)
/ COUNT(case_id) FILTER (
	WHERE (${i2c_s4hc_clearing_date} IS NOT NULL)
) * 100

COUNT(case_id) FILTER (
	WHERE (${i2c_s4hc_clearing_date} - ${i2c_s4hc_due_date} 
		> DURATION ${i2c_s4hc_late_payment_condition})
)
/ COUNT(case_id) FILTER (
	WHERE (${i2c_s4hc_clearing_date} IS NOT NULL)
) * 100

COUNT(case_id) FILTER (
	WHERE (${i2c_s4hc_clearing_date} - ${i2c_s4hc_due_date} 
		< DURATION ${i2c_s4hc_late_payment_condition}
	 AND ${i2c_s4hc_due_date} - ${i2c_s4hc_clearing_date}
		< DURATION ${i2c_s4hc_early_payment_condition}
	)
)
/
COUNT(case_id) FILTER (
	WHERE (${i2c_s4hc_clearing_date} IS NOT NULL)
)
 * 100

COUNT(DISTINCT "ARUniqueKey") FILTER (
	WHERE
		event_name = 'Create Accounts Receivable'
		 AND "ClearingDate" IS NULL
		 AND (NOW()-"DocumentDate") > DURATION ${i2c_s4hc_open_ar_period}
	)

COUNT(DISTINCT "ARUniqueKey") FILTER (
	WHERE event_name = 'Create Accounts Receivable'
	 AND "ClearingDate" IS NULL
	 AND (NOW()-"NetDueDate") > DURATION ${i2c_s4hc_overdue_ar_period}
	)

SUM(${i2c_s4hc_amount_currency}) FILTER (
	WHERE event_name = 'Create Accounts Receivable'
	 AND "ClearingDate" IS NULL
	 AND (NOW()-"NetDueDate") > DURATION ${i2c_s4hc_overdue_ar_period}
	)

SUM(${i2c_s4hc_amount_currency}) FILTER (
	WHERE event_name = 'Clear Accounts Receivable'
	 AND ("ClearingDate" - "NetDueDate") > DURATION ${i2c_s4hc_overdue_ar_period}
	)
