metrics.signalFragment:
MEDIAN(
	(SELECT LAST (end_time) FILTER (
     WHERE event_name IN ('Freight Order Confirmed by Vendor'))
	) 
- 	(SELECT FIRST (end_time) FILTER (
     WHERE event_name IN ('Send Freight Order to Vendor'))
	)
)

MEDIAN(
	(SELECT LAST (end_time) FILTER (
     WHERE event_name IN ('Cancel Execution'))
