metrics.signalFragment:
COUNT(case_id) FILTER (
	WHERE (SELECT BOOL_AND( NOT event_name IN (${lto_sf_close_opp_events})))
	)

COUNT(case_id) FILTER (
	WHERE (SELECT BOOL_OR( event_name IN (${lto_sf_close_opp_events})))
	)

AVG((SELECT COUNT(event_name) FILTER (
	WHERE event_name IN (${lto_sf_touchpoint_events})))
	)

COUNT(case_id) FILTER (
	WHERE 
		(SELECT COUNT(event_name) FILTER 
			(WHERE event_name IN (${lto_sf_touchpoint_events})) 
		) < ${lto_sf_number_of_touchpoints}
		AND (SELECT BOOL_OR(event_name IN (${lto_sf_conversion_events})) 
		) 
	)

COUNT(case_id) FILTER (
	WHERE (SELECT BOOL_OR( event_name IN (${lto_sf_quote_created})))
	)

COUNT(case_id) FILTER (
	WHERE (SELECT BOOL_OR( event_name IN (${lto_sf_quote_approved})))
	)

COUNT(case_id) FILTER (
	WHERE (SELECT BOOL_OR( event_name IN (${lto_sf_quote_rejected})))
	)

SUM("OppAmount") FILTER (
	WHERE (SELECT BOOL_OR( event_name IN (${lto_sf_quote_created})))
	)

AVG("OppAmount") FILTER (
	WHERE (SELECT BOOL_OR( event_name IN (${lto_sf_quote_created})))
	)
