metrics.signalFragment:
AVG(
	(SELECT LAST(end_time)
		WHERE event_name IN (${outbound_delivery_creation_events}) AND
		((NOW() - LAST(end_time)) < DURATION ${outbound_delivery_period})
		)
	-(SELECT FIRST(end_time)
		WHERE event_name IN (${sales_order_item_creation_events})
		)
	)

COUNT(case_id) FILTER (
	WHERE (
		(SELECT BOOL_OR(event_name IN (${sales_order_item_creation_events})) 
		AND ((NOW() - LAST(end_time)) < DURATION ${sales_order_item_creation_period})
	)
	AND (SELECT BOOL_OR(event_name = 'Reject Sales Doc Item')
		AND ((NOW() - LAST(end_time)) < DURATION ${rejected_orders_reference_period}))
	)
)

COUNT(case_id) FILTER (
	WHERE (
		(SELECT BOOL_OR(event_name IN (${sales_order_item_creation_events})) 
		AND ((NOW() - LAST(end_time)) < DURATION ${sales_order_item_creation_period})
	)
	AND (SELECT BOOL_OR(event_name = 'Create Return Sales Order Item')
		AND ((NOW() - LAST(end_time)) < DURATION ${returned_orders_reference_period}))
	)
)

COUNT(case_id) FILTER (
	WHERE (
		SELECT BOOL_OR(event_name IN (${delete_sales_order_item_events}))
		AND NOW() - LAST(end_time) < DURATION ${sales_order_item_deletion_period}
	)
)

COUNT(case_id) FILTER (
	WHERE (SELECT BOOL_OR(event_name IN (${processing_completion_events})
		AND ${ontime_delivery_condition}
		AND event_name NOT ${otc_change_events})
	)
)/COUNT(case_id)
* 100
